// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.11;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

interface NewIdentityContract {
    function createIdentity(
        bytes32 _hashedID,
        address _owner,
        bytes32 _root
    ) external;
}

contract Register is ReentrancyGuard {
    struct registerInfo {
        bytes32 root;
        bytes32 hashedID;
        bool active;
    }
    NewIdentityContract newIdentity;
    mapping(address => registerInfo) register;
    mapping(address => bool) public Trustor;
    mapping(address => bool) public Admin;
    // mapping(address => bool) public registered;

    //**************************************************************
    // Event definitions
    //**************************************************************

    event UserRegistered(bytes32 indexed hashedID, address user, bytes32 root);
    event TrustorSigned(
        bytes32 indexed hashedID,
        address user,
        bool active,
        address trustor
    );
    event TrustorAdded(address trustor);
    event TrustorDisabled(address trustor);
    event AdminAdded(address admin);
    event AdminDisabled(address admin);

    constructor(address[] memory _trustors, address[] memory _admins) {
        for (uint i = 0; i < _trustors.length; i++) {
            Trustor[_trustors[i]] = true;
        }
        for (uint i = 0; i < _admins.length; i++) {
            Admin[_admins[i]] = true;
        }
    }

    modifier onlyAdmin() {
        require(
            Admin[msg.sender] == true,
            "Only the admin can call this function"
        );
        _;
    }

    modifier onlyTrustor() {
        require(
            Trustor[msg.sender] == true,
            "Only the trustor can call this function"
        );
        _;
    }

    //保证address和root是用户自己加的
    function SignByUser(
        bytes32 _hashedID,
        bytes32 _root
    ) external nonReentrant {
        // if(_hashedID.length != 32) {
        //     revert("The length of hashedID is not 32!");
        // }

        //active的address不可以再调用。

        if (register[msg.sender].active == true) {
            revert("The address has been registered!");
        }
        if (
            register[msg.sender].hashedID.length != 32 ||
            register[msg.sender].root.length != 32
        ) {
            revert("The length of hashedID or root is not 32!");
        }

        register[msg.sender].hashedID = _hashedID;
        register[msg.sender].root = _root;
        emit UserRegistered(_hashedID, msg.sender, _root);
    }

    //root信user，hash和active信trustor

    //根据用户hash后的ID和给定的地址，找到用户的address和root并确认未被注册，然后调用NewIdentity合约的createIdentity注册
    //保证active的最后署名是government加的
    function SignByTrustor(
        bytes32 _hashedID,
        address _user,
        address _contractAddress
    ) external onlyTrustor nonReentrant {
        if (register[_user].hashedID != _hashedID) {
            revert("The hashedID has not registered!");
        }
        if (register[_user].root == "") {
            revert("The root has not registered!");
        }
        //防止government作恶，覆盖某用户的address和root
        if (register[_user].active == true) {
            revert("The hashedID has been registered!");
        }
        if (
            register[_user].hashedID.length != 32 ||
            register[_user].root.length != 32
        ) {
            revert("The length of hashedID or root is not 32!");
        }
        register[_user].active = true;
        //send tx to identity contract
        newIdentity = NewIdentityContract(_contractAddress);
        newIdentity.createIdentity(
            register[_user].hashedID,
            _user,
            register[_user].root
        );
        emit TrustorSigned(
            register[_user].hashedID,
            _user,
            register[_user].active,
            msg.sender
        );
    }

    function getRegister(
        address _user
    ) external view returns (registerInfo memory) {
        return register[_user];
    }

    function addTrustor(
        address[] calldata _newtrustor
    ) external onlyAdmin nonReentrant {
        for (uint i = 0; i < _newtrustor.length; i++) {
            Trustor[_newtrustor[i]] = true;

            emit TrustorAdded(_newtrustor[i]);
        }
    }

    function disableTrustor(
        address[] calldata _trustor
    ) external onlyAdmin nonReentrant {
        for (uint i = 0; i < _trustor.length; i++) {
            Trustor[_trustor[i]] = false;

            emit TrustorDisabled(_trustor[i]);
        }
    }

    function addAdmin(
        address[] calldata _newadmin
    ) external onlyAdmin nonReentrant {
        for (uint i = 0; i < _newadmin.length; i++) {
            Admin[_newadmin[i]] = true;

            emit AdminAdded(_newadmin[i]);
        }
    }

    function disableAdmin(
        address[] calldata _admin
    ) external onlyAdmin nonReentrant {
        for (uint i = 0; i < _admin.length; i++) {
            Admin[_admin[i]] = false;

            emit AdminDisabled(_admin[i]);
        }
    }

    /// @dev DON'T give me your money.
    fallback() external {}
}

